options.customCss:
=:root {
	--font-family: 'Inter', 'Open Sans', sans-serif;
	--font-weight-normal: 400;
	--font-weight-bold: 600;
	--font-size-body: 14px;
	--font-size-label: 14px;
	--font-size-header: 26px;
	--font-size-error: 12px;

	/* Tech Gradient Colors */
	--color-primary: #6c63ff;
	--color-secondary: #38bdf8;
	--color-accent: #a855f7;
	--color-background: radial-gradient(circle at top left, #0e0f1a, #1b1e2e);
	--color-card-bg: rgba(255, 255, 255, 0.08);
	--color-card-border: rgba(255, 255, 255, 0.15);
	--color-input-bg: rgba(255, 255, 255, 0.08);
	--color-input-border: rgba(255, 255, 255, 0.25);
	--color-input-text: #e5e7eb;
	--color-label: #c5c7d1;
	--color-header: #ffffff;
	--color-submit-btn-bg: linear-gradient(90deg, var(--color-primary), var(--color-accent));
	--color-submit-btn-bg-hover: linear-gradient(90deg, var(--color-accent), var(--color-secondary));
	--color-submit-btn-text: #ffffff;
	--color-test-notice-bg: rgba(255, 255, 255, 0.08);
	--color-test-notice-text: #eab308;

	--border-radius-card: 16px;
	--border-radius-input: 10px;

	--box-shadow-card: 0 8px 32px rgba(0, 0, 0, 0.25);
	--box-shadow-focus: 0 0 12px var(--color-accent);

	--transition-fast: 0.25s ease;
}

/* Body Background */
body {
	background: var(--color-background);
	font-family: var(--font-family);
	color: var(--color-input-text);
	display: flex;
	justify-content: center;
	align-items: center;
	min-height: 100vh;
	margin: 0;
}

/* Card Container */
form {
	background: var(--color-card-bg);
	border: 1px solid var(--color-card-border);
	backdrop-filter: blur(12px);
	box-shadow: var(--box-shadow-card);
	border-radius: var(--border-radius-card);
	padding: 40px;
	width: 420px;
	display: flex;
	flex-direction: column;
	gap: 18px;
}

/* Header */
form h2 {
	text-align: center;
	color: var(--color-header);
	font-size: var(--font-size-header);
	font-weight: 600;
	margin-bottom: 10px;
	letter-spacing: 1px;
}

/* Labels */
label {
	color: var(--color-label);
	font-size: var(--font-size-label);
	margin-bottom: 6px;
	display: block;
}

/* Inputs, Select, File */
input[type="text"],
input[type="email"],
select,
input[type="file"] {
	width: 100%;
	padding: 12px 14px;
	background: var(--color-input-bg);
	color: var(--color-input-text);
	border: 1px solid var(--color-input-border);
	border-radius: var(--border-radius-input);
	outline: none;
	transition: border var(--transition-fast), box-shadow var(--transition-fast);
}

input:focus,
select:focus {
	border-color: var(--color-accent);
	box-shadow: var(--box-shadow-focus);
}

/* File Input */
input[type="file"]::-webkit-file-upload-button {
	background: var(--color-submit-btn-bg);
	color: #fff;
	border: none;
	padding: 8px 14px;
	border-radius: 6px;
	cursor: pointer;
	transition: background var(--transition-fast);
}
input[type="file"]::-webkit-file-upload-button:hover {
	background: var(--color-submit-btn-bg-hover);
}

/* Submit Button */
button[type="submit"],
input[type="submit"] {
	background: var(--color-submit-btn-bg);
	border: none;
	border-radius: 10px;
	color: var(--color-submit-btn-text);
	font-size: 15px;
	font-weight: 600;
	padding: 14px;
	cursor: pointer;
	transition: all var(--transition-fast);
	box-shadow: 0 4px 16px rgba(108, 99, 255, 0.4);
}

button[type="submit"]:hover,
input[type="submit"]:hover {
	background: var(--color-submit-btn-bg-hover);
	transform: translateY(-1px);
	box-shadow: 0 4px 24px rgba(168, 85, 247, 0.5);
}

/* Test Notice */
.test-notice {
	background: var(--color-test-notice-bg);
	color: var(--color-test-notice-text);
	border: 1px solid rgba(234, 179, 8, 0.3);
	padding: 10px 16px;
	border-radius: 8px;
	font-size: 13px;
	text-align: center;
	margin-bottom: 12px;
}

/* Smooth placeholder */
::placeholder {
	color: rgba(255, 255, 255, 0.5);
	opacity: 1;
}
.subheader {
  text-align: center;
  color: #9ca3af;
  font-size: 13px;
  margin-top: -6px;
  margin-bottom: 16px;
  letter-spacing: 0.5px;
  text-transform: uppercase;
  background: linear-gradient(90deg, #6c63ff, #38bdf8);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  font-weight: 500;
}


/* Subtle fade-in animation */
@keyframes fadeIn {
	from { opacity: 0; transform: translateY(8px); }
	to { opacity: 1; transform: translateY(0); }
}

form {
	animation: fadeIn 0.8s ease forwards;
}

/* ---------- CUSTOM SELECT ---------- */

select {
	background: var(--color-input-bg);
	color: var(--color-input-text);
	border: 1px solid var(--color-input-border);
	border-radius: var(--border-radius-input);
	padding: 12px 14px;
	cursor: pointer;
	appearance: none;
	-webkit-appearance: none;
	-moz-appearance: none;
	position: relative;
	transition: all var(--transition-fast);
}



/* Color al pasar el ratÃ³n o enfocar */
select:hover,
select:focus {
	border-color: var(--color-accent);
	box-shadow: var(--box-shadow-focus);
	color: #ffffff;
}

/* Opciones desplegadas */
option {
	background-color: #1e2030;
	color: #e5e7eb;
	font-size: 14px;
	padding: 10px;
	cursor: pointer;
}

/* OpciÃ³n seleccionada y hover */
option:hover,
option:checked,
option:focus {
	background-color: #2e3350;
	color: #fff;
}

/* Placeholder de select (primera opciÃ³n vacÃ­a) */
select option:first-child {
	color: rgba(255, 255, 255, 0.4);
}

/* Corrige borde inferior mÃ¡s claro en algunos navegadores */
select:focus-visible {
	outline: none;
}

/* AÃ±ade transiciÃ³n suave */
select,
option {
	transition: all 0.2s ease;
}

